.pool
@Credits to Skeli
Move_DOUBLESLAP:
	loadspritegfx ANIM_TAG_IMPACT
	loadspritegfx ANIM_TAG_QUICK_GUARD_HAND
	monbg ANIM_DEF_PARTNER
	setalpha 12, 8
	launchtemplate gDoubleSlapPalmSpriteTemplate, ANIMSPRITE_IS_TARGET | 3, 0x3, -64, 6, TRUE
	delay 8
	playsewithpan SE_M_DOUBLE_SLAP SOUND_PAN_TARGET
	launchtask AnimTask_ShakeMon 0x2 0x5 ANIM_TARGET 2 0 6 1
	launchtemplate gBasicHitSplatSpriteTemplate, ANIMSPRITE_IS_TARGET | 1, 0x4, 0x0 0x0 ANIM_TARGET 0x2
	delay 20
	playsewithpan SE_M_DOUBLE_SLAP SOUND_PAN_TARGET
	launchtask AnimTask_ShakeMon 0x2 0x5 ANIM_TARGET 2 0 6 1
	launchtemplate gBasicHitSplatSpriteTemplate, ANIMSPRITE_IS_TARGET | 1, 0x4, 0x0 0x0 ANIM_TARGET 0x2
	waitforvisualfinish
	clearmonbg ANIM_DEF_PARTNER
	blendoff
	end

.align 2
DOUBLE_SLAP_PALM: objtemplate ANIM_TAG_QUICK_GUARD_HAND ANIM_TAG_QUICK_GUARD_HAND OAM_NORMAL_32x32 gDummySpriteAnimTable 0x0 gSpriteAffineAnimTable_DoubleSlap SpriteCB_WakeUpSlap

