	mov r1, #BATTLE_DOUBLE
	and r0, r1
	cmp r0, #0x0
	beq SolarBeamBeamReturn
	bl IsAnimMoveCoreEnforcer
	cmp r0, #0x0
	bne CoreEnforcerFuncCall

