ENCORE_USER_ASM:
	strh r0, [r5, #0x2e]
	mov r0, r5
	mov r1, #0x0
	bl GetAnimArgBank
	ldr r1, .ReturnEncore
	bx r1

GetAnimArgBank:
	push {r4,r5,lr}
	mov r5, r0
	ldr r4, .AnimAttacker5
	push {r4}
	bl IsAnimMoveOceanicOperretta
	cmp r0, #0x0
	beq DefenderSide

AttackerSide:
	pop {r4}
	b FinishBankArg
DefenderSide:
	pop {r4}
	add r4, #0x1
FinishBankArg:
	ldr r0, .FinishArgReturn
	bx r0

